import React, { useState } from 'react';
import './SignUpPage.css';

const SignUpPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [error, setError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const passwordCriteria = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

  const handleSignUp = () => {
    const passwordCriteria = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    if (!passwordCriteria.test(password)) {
      setPasswordError('생성할 수 없는 비밀번호입니다.');
    } else if (password !== passwordCheck) {
      setPasswordError('');
      setError('비밀번호를 다시 입력해주세요.');
    } else {
      setPasswordError('');
      setError('');
      // 회원가입 성공 로직
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-box">
        <div className="title">Sign up</div>
        <div className="input-box">
          
          <input 
            type="text" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
          />
          
        </div>
        <div className='explain-input'>
          {email}
        </div>
        <div className="input-box">
          <input 
            type="password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
        </div>
        <div>
          {password}
          {passwordCriteria == password}
        </div>
        <div className="input-box">
          <input 
            type="password" 
            value={passwordCheck}
            onChange={(e) => setPasswordCheck(e.target.value)}
            placeholder="Enter your password again"
          />
        </div>
        <div>{passwordCheck}</div>
        {passwordError && <div className="error-message">{passwordError}</div>}
        {error && <div className="error-message">{error}</div>}
        <div className="signup-button" onClick={handleSignUp}>
          Sign up
        </div>
      </div>
    </div>
  );
}

export default SignUpPage;
