// src/HeroComponent.jsx
import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';

const HeroComponent = () => {
  const heroRef = useRef(null);
  const overlayRef = useRef(null);

  const updatePosition = (x, y) => {
    const xPercent = Math.round((x / window.innerWidth) * 100);
    const yPercent = Math.round((y / window.innerHeight) * 100);

    gsap.to(heroRef.current, {
      '--x': `${xPercent}%`,
      '--y': `${yPercent}%`,
      duration: 0.3,
      ease: 'sine.out',
    });
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      e.preventDefault();
      const { clientX, clientY } = e;
      updatePosition(clientX, clientY);
    };

    const handleTouchMove = (e) => {
      e.preventDefault();
      if (e.touches.length > 0) {
        const { clientX, clientY } = e.touches[0];
        updatePosition(clientX, clientY);
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('touchmove', handleTouchMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('touchmove', handleTouchMove);
    };
  }, []);

  const handleClick = (e, href) => {
    e.preventDefault();
    const { clientX, clientY } = e;
    const overlay = overlayRef.current;
    overlay.style.left = `${clientX}px`;
    overlay.style.top = `${clientY}px`;
    overlay.classList.add('expand');

    setTimeout(() => {
      window.location.href = href;
    }, 1000);

    setTimeout(() => {
      overlay.classList.remove('expand');
    }, 1500);
  };

  return (
    <div>
      <div data-hero ref={heroRef} style={{ position: 'relative' }}>
        {/* Hero content here */}
      </div>
      <div ref={overlayRef} className="transition-overlay"></div>
      <button className="btn" href="/some-path" onClick={(e) => handleClick(e, '/some-path')}>Button 1</button>
      <button className="btn" href="/another-path" onClick={(e) => handleClick(e, '/another-path')}>Button 2</button>
    </div>
  );
};

export default HeroComponent;
